pragma solidity ^0.6.12;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";

import "./DuelToken.sol";

contract DuelEvent is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 pendingReward; // Pending rewards
    }

    // Info of each pool.
    struct PoolInfo {
        IERC20 lpToken; // Address of LP token contract.
        uint256 supply; // LP supply for this pool
        uint256 allocPoint; // How many allocation points assigned to this pool. DUELs to distribute per block.
        uint256 lastRewardBlock; // Last block number that DUELs distribution occurs.
        uint256 accTokenPerShare; // Accumulated DUELs per share, times 1e12. See below.
        uint256 totalRewards;
    }

    DuelToken public DUEL;

    uint256 public constant BASE_BP = 10000;
    uint256 public constant STAKE_SHARE = 9900;
    uint256 public constant DEV_SHARE = 50;
    uint256 public constant REF_SHARE = 50;
    address public devAddress;
    address public refAddress;
    uint256 public lastBlockDevWithdraw;

    uint256 public tokenPerBlock;
    uint256 public rewardMultiplier = 1;

    PoolInfo[] public poolInfo;
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
    uint256 public totalAllocPoint = 0;

    uint256 public startBlock;
    uint256 public endBlock;

    uint256 public winnerPoolId;
    bool public winnerSelected = false;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event Claim(address indexed user, uint256 indexed pid, uint256 amount);

    constructor(
        DuelToken _DUEL,
        address _devAddress,
        address _refAddress,
        uint256 _perBlock,
        uint256 _startBlock,
        uint256 _endBlock,
        IERC20 _lpToken,
        uint256[] memory _allocationPoints
    ) public {
        DUEL = _DUEL;
        devAddress = _devAddress;
        refAddress = _refAddress;
        tokenPerBlock = _perBlock;
        startBlock = _startBlock;
        endBlock = _endBlock;
        lastBlockDevWithdraw = _startBlock;
        for (uint256 i = 0; i < _allocationPoints.length; i++) {
            add(_allocationPoints[i], _lpToken, false);
        }
    }

    function withdrawDevAndRefFee() public {
        require(block.number >= startBlock, "DuelEvent: Farming has not started yet");
        require(block.number >= endBlock, "DuelEvent: Farming has not finished yet");
        require(winnerSelected, "DuelEvent: Winner pool has not selected yet");
        require(lastBlockDevWithdraw < block.number, "DuelEvent: Rewards already claimed");

        uint256 toBlock = block.number > endBlock ? endBlock : block.number;

        uint256 multiplier = getMultiplier(lastBlockDevWithdraw, toBlock);
        uint256 reward = multiplier.mul(tokenPerBlock);
        DUEL.mint(devAddress, reward.mul(DEV_SHARE).div(BASE_BP));
        DUEL.mint(refAddress, reward.mul(REF_SHARE).div(BASE_BP));
        lastBlockDevWithdraw = block.number;
    }

    /**
     * @dev Adds a new pool. Can only be called by the owner.
     */
    function add(
        uint256 _allocPoint,
        IERC20 _lpToken,
        bool _withUpdate
    ) private {
        require(!winnerSelected, "DuelEvent: Winner pool has been selected");
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(
            PoolInfo({
                lpToken: _lpToken,
                supply: 0,
                allocPoint: _allocPoint,
                lastRewardBlock: lastRewardBlock,
                accTokenPerShare: 0,
                totalRewards: 0
            })
        );
    }

    /**
     * @dev Updates pool allocation points. Can only be called by the owner.
     */
    function set(
        uint256 _pid,
        uint256 _allocPoint,
        bool _withUpdate
    ) public onlyOwner {
        require(!winnerSelected, "DuelEvent: Winner pool has been selected");
        if (_withUpdate) {
            massUpdatePools();
        }
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_allocPoint);
        poolInfo[_pid].allocPoint = _allocPoint;
    }

    /**
     * @dev Select winner pool. Can only be called by the owner.
     */
    function selectWinner(uint256 _pid) public onlyOwner {
        massUpdatePools();

        winnerSelected = true;
        winnerPoolId = _pid;
    }

    /**
     * @dev Updates reward vairables for all pools. Be careful of gas spending!
     */
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    /**
     * @dev Updates reward vairables for the pool.
     */
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.supply;
        if (lpSupply <= 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 toBlock = block.number > endBlock ? endBlock : block.number;

        uint256 multiplier = getMultiplier(pool.lastRewardBlock, toBlock);
        uint256 reward = multiplier.mul(tokenPerBlock).mul(pool.allocPoint).div(totalAllocPoint).mul(STAKE_SHARE).div(
            BASE_BP
        );

        pool.accTokenPerShare = pool.accTokenPerShare.add(reward.mul(1e12).div(lpSupply));
        pool.lastRewardBlock = block.number;
        pool.totalRewards = pool.totalRewards.add(reward);

        DUEL.mint(address(this), reward);
    }

    /**
     * @dev Returns pending rewards for user.
     */
    function claimAndExit(uint256 _pid) external {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        require(block.number >= startBlock, "DuelEvent: Farming has not started yet");
        require(block.number >= endBlock, "DuelEvent: Farming has not finished yet");
        require(winnerSelected, "DuelEvent: Winner pool has not selected yet");
        require(_pid == winnerPoolId, "DuelEvent: Cannot claim from lose pool");

        updatePool(_pid);

        uint256 userReward = user.pendingReward.add(user.amount.mul(pool.accTokenPerShare).div(1e12));

        uint256 userShare = userReward.div(pool.totalRewards);

        uint256 length = poolInfo.length;

        for (uint256 pid = 0; pid < length; ++pid) {
            if (pid != winnerPoolId) {
                PoolInfo memory _pool = poolInfo[pid];
                userReward = userReward.add(userShare.mul(_pool.totalRewards));
            }
        }

        if (user.amount > 0) {
            pool.supply = pool.supply.sub(user.amount);
            pool.lpToken.safeTransfer(address(msg.sender), user.amount);
            emit Withdraw(msg.sender, _pid, user.amount);
            user.amount = 0;
        }

        safeTokenTransfer(msg.sender, userReward);

        emit Claim(msg.sender, _pid, userReward);
    }

    /**
     * @dev Returns reward multiplier over the given _from to _to block.
     */
    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
        return _to.sub(_from).mul(rewardMultiplier);
    }

    /**
     * @dev Returns pending rewards for user.
     */
    function pendingRewards(uint256 _pid, address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];

        uint256 accTokenPerShare = pool.accTokenPerShare;
        uint256 lpSupply = pool.supply;

        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 toBlock = block.number > endBlock ? endBlock : block.number;
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, toBlock);
            uint256 reward = multiplier
                .mul(tokenPerBlock)
                .mul(pool.allocPoint)
                .div(totalAllocPoint)
                .mul(STAKE_SHARE)
                .div(BASE_BP);
            accTokenPerShare = accTokenPerShare.add(reward.mul(1e12).div(lpSupply));
        }
        return user.amount.mul(accTokenPerShare).div(1e12);
    }

    /**
     * @dev Returns the poolInfo length.
     */
    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    /**
     * @dev Returns the pool specific reward per block.
     */
    function getBaseRewardPerBlock() public view returns (uint256) {
        uint256 multiplier = getMultiplier(block.number.sub(1), block.number);
        return multiplier.mul(tokenPerBlock);
    }

    /**
     * @dev Returns the pool specific reward per block.
     */
    function getPoolRewardPerBlock(uint256 _pid) public view returns (uint256) {
        uint256 multiplier = getMultiplier(block.number.sub(1), block.number);
        return multiplier.mul(tokenPerBlock).mul(poolInfo[_pid].allocPoint).div(totalAllocPoint);
    }

    /**
     * @dev Deposit LP tokens to the pool for reward token allocation.
     */
    function deposit(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        pool.supply = pool.supply.add(_amount);

        updatePool(_pid);

        if (user.amount > 0) {
            user.pendingReward = user.pendingReward.add(user.amount.mul(pool.accTokenPerShare).div(1e12));
        }

        user.amount = user.amount.add(_amount);

        pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);

        emit Deposit(msg.sender, _pid, _amount);
    }

    /**
     * @dev Withdraw LP tokens from the pool.
     */
    function withdraw(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        require(user.amount >= _amount, "DuelEvent: Withdrawal amount exceed balance");

        pool.supply = pool.supply.sub(_amount);

        updatePool(_pid);

        if (user.amount > 0) {
            user.pendingReward = user.pendingReward.add(user.amount.mul(pool.accTokenPerShare).div(1e12));
        }

        user.amount = user.amount.sub(_amount);

        pool.lpToken.safeTransfer(address(msg.sender), _amount);

        emit Withdraw(msg.sender, _pid, _amount);
    }

    /**
     * @dev Withdraw without caring about rewards. EMERGENCY ONLY.
     */
    function emergencyWithdraw(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        pool.supply = pool.supply.sub(user.amount);

        user.amount = 0;
        user.pendingReward = 0;

        pool.lpToken.safeTransfer(address(msg.sender), user.amount);

        emit EmergencyWithdraw(msg.sender, _pid, user.amount);
    }

    /**
     * @dev Safe token transfer function, just in case if rounding error
     * causes pool to not have enough token balance.
     */
    function safeTokenTransfer(address _to, uint256 _amount) internal {
        uint256 balance = DUEL.balanceOf(address(this));
        if (_amount > balance) {
            DUEL.transfer(_to, balance);
        } else {
            DUEL.transfer(_to, _amount);
        }
    }

    /**
     * @dev Updates dev address, only owner.
     */
    function setDevAddress(address _dev) external onlyOwner {
        devAddress = _dev;
    }

    /**
     * @dev Updates ref address, only owner.
     */
    function setRefAddress(address _ref) external onlyOwner {
        refAddress = _ref;
    }

    /**
     * @dev Updates reward per block, only owner.
     */
    function setPerBlock(uint256 _amount) external onlyOwner {
        require(_amount <= 30 * 1e18, "DuelEvent: Max per block");
        require(_amount >= 1 * 1e18, "DuelEvent: Min per block");
        tokenPerBlock = _amount;
    }

    /**
     * @dev Updates reward multiplier, only owner.
     */
    function setRewardMultiplier(uint256 _multiplier) external onlyOwner {
        rewardMultiplier = _multiplier;
    }
}
