","keccak256":"0x8bbbc2f5c10065ee272592ae0a7a6ceb23de2fbd81564ee0bb015ecf404d5f61","license":"MIT"},"contracts/DuelEvent.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.6.12;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

import "./DuelToken.sol";

contract DuelEvent is Ownable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt.
        uint256 pendingReward; // Pending rewards.
        bool claimed;
    }

    // Info of each pool.
    struct PoolInfo {
        IERC20 lpToken; // Address of LP token contract.
        uint256 supply; // LP supply for this pool
        uint256 allocPoint; // How many allocation points assigned to this pool. DUELs to distribute per block.
        uint256 lastRewardBlock; // Last block number that DUELs distribution occurs.
        uint256 accTokenPerShare; // Accumulated DUELs per share, times 1e12. See below.
        uint256 totalRewards; // Total rewards for the pool
    }

    // Basis point base to calculate fees
    uint256 public constant FEE_BASE = 10000;

    // 0.25% claming fee, can be adjusted by the owner (fee <= 100, fee <= 1%)
    uint256 public claimingFee = 25;

    // 4% early withdrawal fee, can be adjusted by the owner (fee <= 1000, fee <= 10%)
    // disabled if winner pool has been selected
    uint256 public withdrawalFee = 400;

    // Address where all fees goes, can be adjusted by the owner
    address public feeRecipient;

    // Reward token instance
    DuelToken public immutable DUEL;

    // Reward token per block, can be adjusted by the owner
    uint256 public tokenPerBlock = 1e18;

    // The block number when rewards starts
    uint256 public startBlock;

    // The block number when rewards ends
    uint256 public endBlock;

    // The block number until deposits are possible
    uint256 public depositEndBlock;

    // Event Result status enum
    enum EventResultStatus {
        Active,
        Win,
        Draw,
        Cancel
    }

    // Event result struct
    struct EventResult {
        uint256 winnerPid;
        EventResultStatus status;
    }

    // Event result to check the status of the event
    EventResult public eventResult;

    // Pools array
    PoolInfo[] public poolInfo;

    // Users mapping, poolId => userAddress => UserInfo
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;

    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event Claim(address indexed user, uint256 indexed pid, uint256 amount);
    event Exit(address indexed user, uint256 indexed pid);
    event LeftoverClaim(address indexed user, uint256 amount);

    constructor(
        DuelToken _DUEL,
        address _feeAddress,
        uint256 _startBlock,
        uint256 _endBlock,
        uint256 _depositEndBlock,
        IERC20 _lpToken,
        uint256[] memory _allocationPoints
    ) public {
        DUEL = _DUEL;
        feeRecipient = _feeAddress;
        startBlock = _startBlock;
        endBlock = _endBlock;
        depositEndBlock = _depositEndBlock;

        for (uint256 i = 0; i < _allocationPoints.length; i++) {
            addPool(_lpToken, _allocationPoints[i]);
        }

        eventResult.status = EventResultStatus.Active;
    }

    /**
     * @dev Updates reward variables for all pools. Be careful of gas spending!
     */
    function massUpdatePools() public {
        for (uint256 pid = 0; pid < poolInfo.length; pid++) {
            updatePool(pid);
        }
    }

    /**
     * @dev Updates reward vairables for the pool.
     */
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];

        if (block.number <= pool.lastRewardBlock || pool.lastRewardBlock >= endBlock) {
            return;
        }

        uint256 lpSupply = pool.supply;

        if (lpSupply <= 0) {
            pool.lastRewardBlock = block.number;
            return;
        }

        uint256 toBlock = block.number > endBlock ? endBlock : block.number;

        uint256 multiplier = getMultiplier(pool.lastRewardBlock, toBlock);
        uint256 reward = multiplier.mul(tokenPerBlock).mul(pool.allocPoint).div(totalAllocPoint);

        pool.accTokenPerShare = pool.accTokenPerShare.add(reward.mul(1e12).div(lpSupply));
        pool.lastRewardBlock = toBlock;
        pool.totalRewards = pool.totalRewards.add(reward);

        DUEL.mint(address(this), reward);
    }

    /**
     * @dev Deposit LP tokens to the pool for reward token allocation.
     */
    function deposit(uint256 _pid, uint256 _amount) external nonReentrant {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(block.number < depositEndBlock, "DuelEvent: Deposit deadline");
        require(_amount > 0, "DuelEvent: Zero amount");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        updatePool(_pid);

        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accTokenPerShare).div(1e12).sub(user.rewardDebt);
            user.pendingReward = user.pendingReward.add(pending);
        }

        user.amount = user.amount.add(_amount);
        user.rewardDebt = user.amount.mul(pool.accTokenPerShare).div(1e12);

        pool.supply = pool.supply.add(_amount);

        pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);

        emit Deposit(msg.sender, _pid, _amount);
    }

    /**
     * @dev Withdraw LP tokens from the pool.
     */
    function withdraw(uint256 _pid, uint256 _amount) external nonReentrant {
        require(_amount > 0, "DuelEvent: Zero amount");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        require(user.amount >= _amount, "DuelEvent: Withdrawal amount exceed balance");

        updatePool(_pid);

        uint256 pending = user.amount.mul(pool.accTokenPerShare).div(1e12).sub(user.rewardDebt);
        user.pendingReward = user.pendingReward.add(pending);

        uint256 fee = getWithdrawalFee(_pid);

        uint256 amount = applyFee(fee, _amount);
        uint256 feeAmount = calculateFee(fee, _amount);

        user.amount = user.amount.sub(_amount);
        user.rewardDebt = user.amount.mul(pool.accTokenPerShare).div(1e12);

        pool.supply = pool.supply.sub(_amount);

        pool.lpToken.safeTransfer(address(msg.sender), amount);
        if (feeAmount > 0) {
            pool.lpToken.safeTransfer(address(feeRecipient), feeAmount);
        }

        emit Withdraw(msg.sender, _pid, amount);
    }

    /**
     * @dev Withdraw without caring about rewards. EMERGENCY ONLY.
     */
    function emergencyWithdraw(uint256 _pid) external {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        pool.supply = pool.supply.sub(user.amount);

        uint256 amount = applyFee(withdrawalFee, user.amount);
        uint256 feeAmount = calculateFee(withdrawalFee, user.amount);

        user.amount = 0;
        user.rewardDebt = 0;
        user.pendingReward = 0;

        pool.lpToken.safeTransfer(address(msg.sender), amount);
        pool.lpToken.safeTransfer(address(feeRecipient), feeAmount);

        emit EmergencyWithdraw(msg.sender, _pid, amount);
    }

    /**
     * @dev Exit pool and claim rewards
     */
    function exit(uint256 _pid) external nonReentrant {
        require(eventResult.status != EventResultStatus.Active, "DuelEvent: Event has not been concluded");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        require(!user.claimed, "DuelEvent: Claimed already");

        updatePool(_pid);

        uint256 userReward = pendingRewards(_pid, msg.sender);

        if (user.amount > 0) {
            uint256 fee = getWithdrawalFee(_pid);

            uint256 amount = applyFee(fee, user.amount);
            uint256 feeAmount = calculateFee(fee, user.amount);

            user.amount = 0;
            pool.supply = pool.supply.sub(user.amount);

            pool.lpToken.safeTransfer(address(msg.sender), amount);

            if (feeAmount > 0) {
                pool.lpToken.safeTransfer(address(feeRecipient), feeAmount);
            }

            emit Withdraw(msg.sender, _pid, amount);
        }

        user.claimed = true;

        if (userReward > 0) {
            safeTokenTransfer(msg.sender, userReward);
            emit Claim(msg.sender, _pid, userReward);
        }

        emit Exit(msg.sender, _pid);
    }

    /**
     * @dev Returns reward multiplier over the given _from to _to block.
     */
    function getMultiplier(uint256 _from, uint256 _to) public pure returns (uint256) {
        return _to.sub(_from);
    }

    /**
     * @dev Returns pending rewards for user.
     */
    function pendingRewards(uint256 _pid, address _user) public view returns (uint256) {
        PoolInfo memory pool = poolInfo[_pid];
        UserInfo memory user = userInfo[_pid][_user];

        if (block.number < startBlock || user.claimed) {
            return 0;
        }

        uint256 accTokenPerShare = pool.accTokenPerShare;
        uint256 lpSupply = pool.supply;
        uint256 userReward = user.pendingReward;

        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 toBlock = block.number > endBlock ? endBlock : block.number;
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, toBlock);
            uint256 reward = multiplier.mul(tokenPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
            accTokenPerShare = accTokenPerShare.add(reward.mul(1e12).div(lpSupply));
        }

        userReward = userReward.add(user.amount.mul(accTokenPerShare).div(1e12).sub(user.rewardDebt));

        if (eventResult.status == EventResultStatus.Win && eventResult.winnerPid == _pid) {
            uint256 userShare = userReward.mul(1e12).div(pool.totalRewards);
            for (uint256 pid = 0; pid < poolInfo.length; pid++) {
                if (pid != eventResult.winnerPid) {
                    PoolInfo memory _pool = poolInfo[pid];
                    userReward = userReward.add(userShare.mul(_pool.totalRewards).div(1e12));
                }
            }
        } else if (
            (eventResult.status == EventResultStatus.Win && eventResult.winnerPid != _pid) ||
            eventResult.status == EventResultStatus.Cancel
        ) {
            userReward = 0;
        }

        return userReward;
    }

    /**
     * @dev Returns the poolInfo length.
     */
    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    /**
     * @dev Returns base reward per block.
     */
    function getBaseRewardPerBlock() external view returns (uint256) {
        return tokenPerBlock;
    }

    /**
     * @dev Returns the pool specific reward per block.
     */
    function getPoolRewardPerBlock(uint256 _pid) external view returns (uint256) {
        return tokenPerBlock.mul(poolInfo[_pid].allocPoint).div(totalAllocPoint);
    }

    /**
     * @dev Sets event status to EventResultStatus.Win and selects winner pool. Only owner.
     */
    function setWin(uint256 _pid) public onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(block.number >= depositEndBlock, "DuelEvent: Deposit deadline not reached");
        massUpdatePools();
        eventResult.status = EventResultStatus.Win;
        eventResult.winnerPid = _pid;
    }

    /**
     * @dev Sets event status to EventResultStatus.Draw. Only owner.
     */
    function setDraw() public onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(block.number >= depositEndBlock, "DuelEvent: Deposit deadline not reached");
        massUpdatePools();
        eventResult.status = EventResultStatus.Draw;
    }

    /**
     * @dev Sets event status to EventResultStatus.Cancel. Only owner.
     */
    function setCancel() public onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(block.number >= depositEndBlock, "DuelEvent: Deposit deadline not reached");
        massUpdatePools();
        eventResult.status = EventResultStatus.Cancel;
    }

    /**
     * @dev Updates pool allocation points, only owner.
     */
    function setPool(
        uint256 _pid,
        uint256 _alloc,
        bool _withUpdate
    ) public onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        if (_withUpdate) {
            massUpdatePools();
        }
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_alloc);
        poolInfo[_pid].allocPoint = _alloc;
    }

    /**
     * @dev Sends leftover tokens to the fee recipient, only owner.
     */
    function claimLeftovers() external onlyOwner {
        require(eventResult.status != EventResultStatus.Active, "DuelEvent: Event has not been concluded");
        require(poolInfo[0].lpToken.balanceOf(address(this)) == 0, "DuelEvent: Not all users has claimed");

        uint256 balance = DUEL.balanceOf(address(this));
        require(balance > 0, "DuelEvent: Zero balance");

        safeTokenTransfer(feeRecipient, balance);
        emit LeftoverClaim(feeRecipient, balance);
    }

    /**
     * @dev Updates claming fee, max is 1%, only owner.
     */
    function setClaimingFee(uint256 _fee) external onlyOwner {
        require(_fee <= 100, "DuelEvent: Max claming fee");
        claimingFee = _fee;
    }

    /**
     * @dev Updates withdrawal fee, max is 10%, only owner.
     */
    function setWithdrawalFee(uint256 _fee) external onlyOwner {
        require(_fee <= 1000, "DuelEvent: Max withdrawal fee");
        withdrawalFee = _fee;
    }

    /**
     * @dev Updates fee recipient, only owner.
     */
    function setFeeRecipient(address _feeRecipient) external onlyOwner {
        require(_feeRecipient != address(0), "DuelEvent: Zero fee recipient");
        feeRecipient = _feeRecipient;
    }

    /**
     * @dev Updates reward per block, only owner.
     */
    function setTokenPerBlock(uint256 _amount) external onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(_amount <= 30 * 1e18, "DuelEvent: Max per block");
        require(_amount >= 1 * 1e18, "DuelEvent: Min per block");
        tokenPerBlock = _amount;
    }

    /**
     * @dev Updates start block, only owner.
     */
    function setStartBlock(uint256 _block) external onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(startBlock > block.number, "DuelEvent: Event has been started");
        require(_block < endBlock, "DuelEvent: Start block should be less then endBlock");

        startBlock = _block;

        for (uint256 pid = 0; pid < poolInfo.length; pid++) {
            poolInfo[pid].lastRewardBlock = startBlock;
        }
    }

    /**
     * @dev Updates end block, only owner.
     */
    function setEndBlock(uint256 _block) external onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(endBlock > block.number, "DuelEvent: Event has been finished");
        require(_block > startBlock, "DuelEvent: End block should be greater then startBlock");

        endBlock = _block;
    }

    /**
     * @dev Updates deposit end block, only owner.
     */
    function setDepositEndBlock(uint256 _block) external onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(_block > startBlock && _block < endBlock, "DuelEvent: Wrong deposit end block");

        depositEndBlock = _block;
    }

    /**
     * @dev Adds a new pool, internal.
     */
    function addPool(IERC20 _lp, uint256 _alloc) internal {
        totalAllocPoint = totalAllocPoint.add(_alloc);
        poolInfo.push(
            PoolInfo({
                lpToken: _lp,
                supply: 0,
                allocPoint: _alloc,
                lastRewardBlock: block.number > startBlock ? block.number : startBlock,
                accTokenPerShare: 0,
                totalRewards: 0
            })
        );
    }

    /**
     * @dev Safe token transfer function, just in case if rounding error
     * causes pool to not have enough token balance.
     */
    function safeTokenTransfer(address _to, uint256 _amount) internal {
        uint256 balance = DUEL.balanceOf(address(this));
        uint256 amount = _amount > balance ? balance : _amount;
        require(DUEL.transfer(_to, amount), "DuelEvent: safeTokenTransfer error");
    }

    /**
     * @dev it calculates (1 - fee) * amount
     * Applies the fee by subtracting fees from the amount and returns
     * the amount after deducting the fee.
     */
    function applyFee(uint256 _feeInBips, uint256 _amount) internal pure returns (uint256) {
        return _amount.mul(FEE_BASE.sub(_feeInBips)).div(FEE_BASE);
    }

    /**
     * @dev it calculates fee * amount
     * Calculates the fee amount.
     */
    function calculateFee(uint256 _feeInBips, uint256 _amount) internal pure returns (uint256) {
        return _amount.mul(_feeInBips).div(FEE_BASE);
    }

    /**
     * @dev Get withdrawal fee in basis points for the user of the given pool.
     */
    function getWithdrawalFee(uint256 _pid) internal view returns (uint256) {
        if (eventResult.status != EventResultStatus.Active) {
            if (
                (eventResult.status == EventResultStatus.Win && _pid == eventResult.winnerPid) ||
                eventResult.status == EventResultStatus.Draw
            ) {
                return claimingFee;
            } else {
                return 0;
            }
        } else {
            return withdrawalFee;
        }
    }
}
