pragma solidity ^0.6.12;

import "./ERC20Mintable.sol";
import "./TransactionThrottler.sol";

contract DuelToken is ERC20Mintable, TransactionThrottler {
    constructor() public ERC20Mintable("Duel Network", "DUEL") {
        _mint(msg.sender, 20000000e18);
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override transactionThrottler(sender, recipient, amount) {
        super._transfer(sender, recipient, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override(ERC20Mintable) {
        super._beforeTokenTransfer(from, to, amount);
    }
}
